# Ruta donde se guardar√° el archivo de cookies en formato Netscape
output_file = "cookies.txt"

# Convertir a formato Netscape
with open(output_file, "w") as f:
    # Escribir el encabezado del archivo Netscape
    f.write("# Netscape HTTP Cookie File\n")
    f.write("# This is a generated file! Do not edit.\n\n")

    for cookie in cookies_json:
        domain = cookie["domain"]
        flag = "TRUE" if not cookie["hostOnly"] else "FALSE"
        path = cookie["path"]
        secure = "TRUE" if cookie["secure"] else "FALSE"
        expiration = str(int(cookie["expirationDate"]))  # Convertir a entero
        name = cookie["name"]
        value = cookie["value"]
        
        # Escribir la cookie en formato Netscape
        f.write(f"{domain}\t{flag}\t{path}\t{secure}\t{expiration}\t{name}\t{value}\n")

print(f"Cookies guardadas en formato Netscape en {output_file}")